mapper.insert:
INSERT INTO WULIU_ORDER_DETAIL (gmt_create , gmt_modified , main_order_id , count , length , width , height , weight , status)
VALUES (now() , now() , #{mainOrderId} , #{count} , #{length} , #{width} ,
#{height} , #{weight} , #{status});

mapper.select:
SELECT id as id, main_order_id as mainOrderId, count as count, length as length, width as width, height as height, weight as weight, status as status FROM WULIU_ORDER_DETAIL
id = #{id}
AND main_order_id = #{mainOrderId}
AND status = #{status}
ORDER BY ID ASC limit #{start} , #{size}

SELECT
	COUNT(*)
FROM WULIU_ORDER_DETAIL


		id = #{id}


		AND main_order_id = #{mainOrderId}


		AND status = #{status}
